import React, { useState } from 'react' ;
import AppLayout from '../components/AppLayout' ;
import Head from 'next/head' ;
import { Form, Input, Checkbox, Button } from 'antd' ;

const signup = () => {

    const [id, setId] = useState('') ;
    const [nick, setNick] = useState('') ;
    const [password, setPassword] = useState('') ;
    const [passwordCheck, setPasswordCheck] = useState('') ;
    const [term, setTerm] = useState(false) ;

    const onChangeId = () => {} 

    const onChangeNick = () => {}

    const onChangePassWord = () => {

    }

    const onChangePassWordChk = () => {}

    const onChangeTerm = () => {}

    const onSubmit = () => {}

    return(
    <>
        <Head>
            <title>React-Twitter</title>
            <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.23.3/antd.css" />
        </Head>
        <AppLayout>
            <Form onSubmit={onSubmit} style={{ padding: 10 }}>
                <div>
                    <label htmlFor="user-id">아이디</label>
                    <br />
                    <Input name="user-id" required onChange={onChangeId} />
                </div>
                <div>
                    <label htmlFor="user-nick">닉네임</label>
                    <br />
                    <Input name="user-nick" required onChange={onChangeNick} />
                </div>
                <div>
                    <label htmlFor="user-pass">비밀번호</label>
                    <br />
                    <Input name="user-pass" type="password" required onChange={onChangePassWord} />
                </div>
                <div>
                    <label htmlFor="user-pass">비밀번호 체크</label>
                    <br />
                    <Input name="user-pass-chk" type="password" required onChange={onChangePassWordChk} />
                </div>
                <div>
                    <Checkbox name="user-term" value="term" onChange={onChangeTerm}>1일 1컷일을 할 것을 동의합니다. </Checkbox>
                </div>
                <div>
                    <Button type="primary" htmlType="submit">가입하기</Button>
                </div>
            </Form>
        </AppLayout>
    </>
    ) ;
} ;

export default signup ;
